    def set_language(self, lang_code):
        if lang_code in self.languages:
            self.current_language = lang_code
            return f"Language set to {lang_code}"
        else:
            return "Language not available."
        
    def get_translation(self, lang_code, *keys):
        translation = self.languages.get(lang_code, {})
        for key in keys:
            translation = translation.get(key, "Translation not found.")
            if translation == "Translation not found.":
                break
        return translation
    